import { Link, NavLink } from "react-router-dom";
import "./Header.css";

export default function Header() {
	let activeStyle = {
		textDecoration: "underline",
	};

	let guestNavigation = (
		<div id="guest">
			<NavLink className="button" to="/login" style={({ isActive }) => (isActive ? activeNavStyle : undefined)}>
				Login
			</NavLink>
			<NavLink className="button" to="/register" activeStyle={{ color: "red" }}>
				Register
			</NavLink>
		</div>
	);

	return (
		<header id="site-header">
			<nav className="navbar">
				<Link to="/">
					<img src="/images/ecolife-logo.jpg" alt="logo" />
				</Link>
				<section className="main-navbar">
					<Link className="button" to="/">
						Home
					</Link>
					<Link className="button" to="/about">
						About
					</Link>
					<Link className="button" to="/shop">
						Shop
					</Link>
					<Link className="button" to="/contacts">
						Contact us
					</Link>
				</section>
				<section className="user-navbar">{guestNavigation}</section>
			</nav>
		</header>
	);
}
